import React, { Component } from 'react';
import axios from 'axios'




export default class Annualpage extends Component {

    constructor(props) {
        var token = localStorage.getItem('usertoken');
        var localdata = JSON.parse(token)

        super(props);
        this.state = {
            State: localdata.state,
            city: localdata.city,
            getcitiescode: [],
            currentcitycode: '',
            weatherdata: []
        }
        // console.log(this.state.city)

    }

    componentDidMount() {
        axios.get('http://agrolly.tech/get_cities.php')
            .then(response => {
                const getcities = response.data
                // console.log(getcities)
                this.setState({ getcitiescode: getcities })
                this.getCityCode();
            }).catch((error) => {
                console.log("error from get cities: " + error)
            })
        axios.get('http://agrolly.tech/annualForecast.php?country=Brazil')
            .then(response => {
                console.log(response)
            })

    }

    getCityCode() {
        this.state.getcitiescode.map((currentcity) => {
            if (currentcity.City == this.state.city) {
                const currentcitycode = currentcity.Code
                console.log(currentcitycode)
                this.setState({ currentcitycode: currentcitycode })
            }
        })
    }

    OnChangeState(e) {
        this.setState({
            state: e.target.value
        })
    }

    OnChnageCity(e) {
        this.setState({
            city: e.target.value
        })
    }

    OnSubmit(e) {

    }
    render() {
        return (
            <div >

                <form onSubmit={this.onSubmit()}>
                    <div class="input-group mb-3">
                        <div class="input-group-prepend">
                            <label class="input-group-text" for="inputCountry">Options</label>
                        </div>
                        <select class="custom-select" id="inputCountry">
                            <option selected>Choose...</option>
                            <option value="Brazil">Brazil</option>
                            <option value="Mongolia">Mongolia</option>
                            <option value="USA">United State</option>
                            <option value="Taiwan">Taiwan</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="question sent"
                            className="btn btn-primary" />
                    </div>
                </form>
                <div>a page to dispay annual weatehr</div>
            </div>
        )
    }
}
