import React, { Component ,useState } from 'react';
import axios from 'axios'
import VegetablesCard from './vegetableCard'




export default class riskpage extends Component {

    constructor(props) {
        super(props);

        const state = this.props.location.state.detail;

        this.state = {
            month: state.month,
            po_place: state.po_place,
            CityCode: state.CityCode,

            vegetables: [],
            allcityLong: [],
            citydata: [],

            weatherdata: [],
            avaTemp:[],

            monthava:[],

            getava1:""

        }

    }

    componentDidMount() {
        axios.get('http://www.agrolly.tech/po_value.php')
            .then(res => {
                // console.log(res.data)
                const po = res.data
                this.setState({ allcityLong: po })
                this.findcityLong()
            }).catch(error => {
                console.log(error)
            })

        //get crop detail
        axios.get('http://www.agrolly.tech/cropdata.php')
            .then(res => {
                // console.log(res.data)
                const vegetables = res.data
                this.setState({ vegetables: vegetables })
            })
            .catch(error => {
                console.log('error come from get crop api: ' + error)
            })
        // axios.get('http://agrolly.tech/annualForecast.php?country=Brazil')
        //     .then(responsedata => {
        //         // console.log(responsedata)
        //         const weatherdata = responsedata.data
        //         this.setState({ weatherdata: weatherdata })
        //     })
        // {this.AverageTemp()}
    }


    findcityLong() {
        const long = this.state.po_place.slice(2)
        // const NorS = this.state.po_place.subString(0)
        return this.state.allcityLong.filter(citylong => {
            if (citylong.city_long === long) {
                // console.log(citylong)
                this.setState({ citydata: citylong })
            }
        })
    }

    vegetablesList() {
        return this.state.vegetables.map((vege, index) => {
            // console.log(vege)
            return <VegetablesCard
                key={index}
                vege={vege}
                cityPo={this.state.po_place}
                selectMount={this.state.month}
                citydata={this.state.citydata} 
                CityCode={this.state.CityCode}
                findavaTemp={this.AverageTemp}/>
        })
    }

    vegetablesList() {
        return this.state.vegetables.map((vege, index) => {
            // console.log(vege)
            return <VegetablesCard
                key={index}
                vege={vege}
                cityPo={this.state.po_place}
                selectMount={this.state.month}
                citydata={this.state.citydata} 
                CityCode={this.state.CityCode}
                findavaTemp={this.AverageTemp}/>
        })
    }

    AverageTemp() {
            let month1 =0, count1 = 0, getava1 = 0
            let month2 =0, count2 = 0, getava2 = 0
            let month3 =0, count3 = 0, getava3 = 0
            let month4 =0, count4 = 0, getava4 = 0
            let month5 =0, count5 = 0, getava5 = 0
            let month6 =0, count6 = 0, getava6 = 0
            let month7 =0, count7 = 0, getava7 = 0
            let month8 =0, count8 = 0, getava8 = 0
            let month9 =0, count9 = 0, getava9 = 0
            let month10 =0, count10 = 0, getava10 = 0
            let month11 =0, count11 = 0, getava11 = 0
            let month12 =0, count12 = 0, getava12 = 0

            const monthlyava =  this.state.weatherdata.map(data => {
                const date = data["Date.fcst"]
                // console.log(date)
                const AVAtemperature = "TEMPMEDIA_fcast_" + this.state.CityCode
                // console.log(data[AVAtemperature])
                if(date.substring(5, 7).includes("01")){
                    month1 = data[AVAtemperature] + month1
                    count1++
                    getava1 = month1/count1;
                }else if(date.substring(5, 7).includes("02")){
                    month2 = data[AVAtemperature] + month2
                    count2++
                    getava2 = month2/count2;
                }else if(date.substring(5, 7).includes("03")){
                    month3 = data[AVAtemperature] + month3
                    count3++
                    getava3 = month3/count3;
                }else if(date.substring(5, 7).includes("04")){
                    month4 = data[AVAtemperature] + month4
                    count4++
                    getava4 = month4/count4;
                }else if(date.substring(5, 7).includes("05")){
                    month5 = data[AVAtemperature] + month5
                    count5++
                    getava5 = month5/count5;
                }else if(date.substring(5, 7).includes("06")){
                    month6 = data[AVAtemperature] + month6
                    count6++
                    getava6 = month6/count6;
                }else if(date.substring(5, 7).includes("07")){
                    month7 = data[AVAtemperature] + month7
                    count7++
                    getava7 = month7/count7;
                }else if(date.substring(5, 7).includes("08")){
                    month8 = data[AVAtemperature] + month8
                    count8++
                }else if(date.substring(5, 7).includes("09")){
                    month9 = data[AVAtemperature] + month9
                    count9++
                }else if(date.substring(5, 7).includes("10")){
                    month10 = data[AVAtemperature] + month10
                    count10++
                }else if(date.substring(5, 7).includes("11")){
                    month11 = data[AVAtemperature] + month11
                    count11++
                }else if(date.substring(5, 7).includes("12")){
                    month12 = data[AVAtemperature] + month12
                    count12++
                }
            //    this.setState({})
            })
            this.setState({getava1:getava1})
            console.log(month3/count3)

            return monthlyava

        }



    render() {
        return (
            <div>
            <h1>test: {this.state.getava1}</h1>
            {/* {this.state.avaTemp.map(test => <h2>{test}</h2>)} */}
                {this.vegetablesList()}
                {/* {this.AverageTemp()} */}

            </div>
        )
    }
}
