import React, { Component } from 'react';
import axios from 'axios';


export default class OneTimePassword extends Component {

    constructor(props) {
        super(props);

        this.onChangeEmail = this.onChangeEmail.bind(this)
        this.onChangeOneTime = this.onChangeOneTime.bind(this)


        this.state = {
            useremail: '',
            oneTimepassword: '',
        }
    }

    onChangeEmail(e) {
        this.setState({
            useremail: e.target.value
        });
    }

    onChangeOneTime(e) {
        this.setState({
            oneTimepassword: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const oneTimeLogin = {
            useremail: this.state.useremail,
            oneTimepassword: this.state.oneTimepassword,
        }

        axios
            .post('http://agrolly.tech/mail.php', oneTimeLogin, {
                headers: {
                    'Content-type': 'application/x-www-form-urlencoded'
                }})
            .then(response => {
                // localStorage.setItem('usertoken', JSON.stringify(response.data))
                console.log(response.data)
                // window.location = "/farmer/login"
            })
            .catch(err => {
                console.log(err)
            })


    }




    render() {
        return (
            <div className="container" >
                <form onSubmit={this.onSubmit} >
                    <div className="form-group">
                        <input type="text" className="form-control"
                            placeholder="Email"
                            value={this.state.useremail}
                            onChange={this.onChangeEmail} />
                    </div>


                    <div className="form-group">
                        <input type="password" className="form-control"
                            placeholder="One Time password"
                            value={this.state.oneTimepassword}
                            onChange={this.onChangeOneTime} />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="submit"
                            className="btn btn-primary" />
                    </div>

                </form>

            </div>
        );
    }
}
