import React, { Component } from 'react';
import axios from 'axios';
import style from './register.module.css';

import logo from '../../Image/Agrolly/Agrolly logo.png'

export default class forgetPassword extends Component {

    constructor(props) {
        super(props);

        this.onChangeEmail = this.onChangeEmail.bind(this)
        this.onChangePassword = this.onChangePassword.bind(this)
        this.onSubmit = this.onSubmit.bind(this)

        this.state = {
            useremail: '',
            userpassword: '',
        }
    }

    onChangeEmail(e) {
        this.setState({
            useremail: e.target.value
        });
    }

    onChangePassword(e) {
        this.setState({
            userpassword: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const reset = {
            useremail: this.state.useremail,
            userpassword: this.state.userpassword,
        }

        axios
            .post('http://agrolly.tech/mail.php', userlogin, {
                headers: {
                    'Content-type': 'application/x-www-form-urlencoded'
                }})
            .then(response => {
                console.log(response.data)
                this.props.history.push({
                    pathname: '/farmer/passwordreset',
                    state: { detail: reset }
                  })
            })
            .catch(err => {
                console.log(err)
            })


    }




    render() {
        return (
            <div className={style.backgroundLogin} >

            <div className={style.formcontainer}>
                <img style = {{"width":"40%", "marginBottom":"10px"}} src={logo} alt="logo"/>
                <form onSubmit={this.onSubmit} >
                    <div className="form-group">
                        <input type="text" className="form-control"
                            placeholder="Email"
                            value={this.state.useremail}
                            onChange={this.onChangeEmail} />
                    </div>


                    <div className="form-group">
                        <input type="password" className="form-control"
                            placeholder="password"
                            value={this.state.userpassword}
                            onChange={this.onChangePassword} />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="login"
                            className="btn btn-primary" />
                    </div>

                </form>

                <h6>don't have an account yet? click <a style ={{"color": "blue"}} href="/register">here</a> to register one</h6>
                <h6><a style ={{"color": "blue"}} href="/register">forget password</a></h6>
                </div>

            </div>
        );
    }
}
