import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';


let forumCard = {
    maxWidth: "100%",
    backgroundColor:"#eee",
    margin:"10px",
    padding:"10px"
}
let questiontext = {
    fontWeight: '700',
    fontSize:"20px"
}

function ChatCard(props) {
    function checkID(){
        // console.log(props.chat.id)
        // const chatanwer = 0
        const addfavorite = {
            user_id: props.user.rid,
        }
        axios.get('http://agrolly.tech/quesComm.php?what=comment&id=' + props.chat.id)
            .then(res => {
                const chatanwer = res.data[0].user_id;
                addfavorite.user_id = chatanwer
            }).catch((error) => {
                console.log("errow from chat room answer: " + error)
            })
        console.log(addfavorite)
    }

    return (
        <div style={forumCard}>
        {checkID()}
            <button onClick={()=>props.deleteQuestion(props.chat.id)}>delete</button>
            <Link to={"/forum/joinchat/" + props.chat.id}>
                <p style={questiontext}>{props.chat.question}</p>
                <p>{props.chat.name}</p>
                <p>{props.chat.state}</p>
                <p>{props.chat.date}</p>
            </Link>
        </div>
    )
}

export default ChatCard;
