import React, { Component } from 'react';
import style from '../farmer/register.module.css';
import axios from 'axios';

export default class ProfileEdit extends Component {
    constructor(props) {

        super(props)
        this.state = {
            user_id: '',
            name: '',
            country: '',
            city: '',
            state: '',
            user: '',
            upassword:''
        }
    }

    componentDidMount() {
        var token = localStorage.getItem('usertoken');
        var passwordtoken = localStorage.getItem('passwordtoken')
        console.log(passwordtoken)
        console.log(token)
        var localdata = JSON.parse(token)
        var JsonPassword = JSON.parse(passwordtoken)
        this.setState({
            user_id: localdata.user_id,
            name: localdata.name,
            country: localdata.country,
            city: localdata.city,
            state: localdata.state,
            user: localdata.user,
            upassword :JsonPassword.userpassword
        })
    }

    onChangeName(e) {
        this.setState({
            username: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const EditProfile = {
            uid: this.state.user_id,
            uname: this.state.name,
            upassword: this.state.upassword,
            uemail: this.state.name,
            ucountry: this.state.country,
            ustate: this.state.state,
            ucity: this.state.city,
        }





        // axios
        //     .post('http://agrolly.tech/mail.php', {
        //         headers: {
        //             'Content-type': 'application/x-www-form-urlencoded'
        //         }
        //     })
        //     .then(response => {
        //         console.log(response.data)
        //         this.props.history.push({
        //             pathname: '/farmer/onetimepassword',
        //             state: { detail: userRegister }
        //           })

        //     })
        //     .catch(err => {
        //         console.log(err)
        //     })


    }


    render() {
        return (
            <div>
                <div className = {style.ProfileCard}>
                    <form onSubmit={this.onSubmit}>
                    <h1>Your Profile</h1>
                    <input type="text" className="form-control"
                            placeholder="Email"
                            value={this.state.name}
                            onChange={this.onChangeName} />
                    <hr />
                    <h5>Account Email</h5>
                    <p>{this.state.user}</p>
                    <hr />
                    <h5>Country</h5>
                    <p>{this.state.country} </p>
                    <hr />
                    <h5>State</h5>
                    <p>{this.state.state} </p>
                    <hr />
                    <h5>City</h5>
                    <p>{this.state.city} </p>

                    <div className="form-group">
                        <input type="submit" value="Edit"
                            className="btn btn-primary" />
                    </div>

                    </form>
                </div>
            </div>
        )
    }
}
