        this.onChangeOneTime = this.onChangeOneTime.bind(this)

        this.state = {
            useremail: "",
            username: "",
            userpassword: "",
            usercountry: "",
            userstate: "",
            usercity: "",
            onetimepassword: "",
        }

    }

    onChangeEmail(e) {
        this.setState({
            useremail: e.target.value
