import React, { Component } from 'react';
import axios from 'axios'
import VegetablesCard from './vegetableCard'




export default class riskpage extends Component {

    constructor(props) {
        super(props);

        const state = this.props.location.state.detail;

        this.state = {
            month: state.month,
            po_place: state.po_place,
            CityCode: state.CityCode,

            vegetables: [],
            allcityLong: [],
            citydata: [],

            weatherdata: [],
            avaTemp:[],

            monthava:[],
        }

    }

    componentDidMount() {
        axios.get('http://www.agrolly.tech/po_value.php')
            .then(res => {
                // console.log(res.data)
                const po = res.data
                this.setState({ allcityLong: po })
                this.findcityLong()
            }).catch(error => {
                console.log(error)
            })

        //get crop detail
        axios.get('http://www.agrolly.tech/cropdata.php')
            .then(res => {
                // console.log(res.data)
                const vegetables = res.data
                this.setState({ vegetables: vegetables })
            })
            .catch(error => {
                console.log('error come from get crop api: ' + error)
            })
        axios.get('http://agrolly.tech/annualForecast.php?country=Brazil')
            .then(responsedata => {
                console.log(responsedata)
                const weatherdata = responsedata.data
                this.setState({ weatherdata: weatherdata })
            })
        // {this.AverageTemp()}
    }


    findcityLong() {
        const long = this.state.po_place.slice(2)
        // const NorS = this.state.po_place.subString(0)
        return this.state.allcityLong.filter(citylong => {
            if (citylong.city_long === long) {
                // console.log(citylong)
                this.setState({ citydata: citylong })
            }
        })
    }

    vegetablesList() {
        return this.state.vegetables.map((vege, index) => {
            // console.log(vege)
            return <VegetablesCard
                key={index}
                vege={vege}
                cityPo={this.state.po_place}
                selectMount={this.state.month}
                citydata={this.state.citydata} 
                CityCode={this.state.CityCode}
                findavaTemp={this.AverageTemp}/>
        })
    }





    render() {
        return (
            <div>
            {/* {this.state.avaTemp.map(test => <h2>{test}</h2>)} */}
                {this.vegetablesList()}
                {/* {this.AverageTemp(2)}
                 */}
            </div>
        )
    }
}
