import React, { Component } from 'react';
import axios from 'axios'


export default class OneTimePassword extends Component {

    constructor(props) {
        super(props);

        this.onChangeEmail = this.onChangeEmail.bind(this)
        this.onChangeOneTime = this.onChangeOneTime.bind(this)
        const state = this.props.location.state
        console.log(state)
        this.state = {
            useremail:"",
            username: "",
            userpassword: "",
            usercountry: "",
            userstate: "",
            usercity: "",
            onetimepassword: "" ,
            enterpassword:""
        }

    }

    onChangeEmail(e) {
        this.setState({
            useremail: e.target.value
        });
    }

    onChangeOneTime(e) {
        this.setState({
            enterpassword: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const userRegister = {
            useremail: this.state.useremail,
            userpassword: this.state.userpassword,
            username: this.state.username,
            usercountry: this.state.usercountry,
            userstate: this.state.userstate,
            usercity: this.state.usercity,
            onetimepassword: this.state.onetimepassword
        }


        axios
            .post('http://agrolly.tech/register.php', userRegister, {
                headers: {
                    'Content-type': 'application/x-www-form-urlencoded'
                }})
            .then(response => {
                // localStorage.setItem('usertoken', JSON.stringify(response.data))
                console.log(response.data)
                // window.location = "/farmer/login"
            })
            .catch(err => {
                console.log(err)
            })


    }




    render() {
        // const { data } = this.props.location
        return (

            <div className="container" >
                <form onSubmit={this.onSubmit} >
                    {/* <h1>{data.useremail}</h1> */}
                    <div className="form-group">
                        <input type="password" className="form-control"
                            placeholder="One Time password"
                            value={this.state.enterpassword}
                            onChange={this.onChangeOneTime} />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="submit"
                            className="btn btn-primary" />
                    </div>

                </form>

            </div>
        );
    }
}
