import React from 'react';
import axios from 'axios';


function MyQuestionCard(props) {

        this.state = {
            answerdata:[]
        }

        axios.get('http://agrolly.tech/quesComm.php?what=comment&id='+props.question.id)
        .then(response =>{
             const answerdata = response.data
             this.setState({ answerdata: answerdata });

        })
        console.log(this.state.answerdata)







    return (
        <div >   
                <p>{props.question.question}</p>
                <p>{props.question.date}</p>

        </div>
    )
}

export default MyQuestionCard;
