import React, { Component } from 'react'
import axios from 'axios';
import Card from './newsCards'

export default class NewsComponent extends Component {

    constructor(props) {
        super(props);
        this.state = { news: [] };
    }

    componentDidMount() {


        axios.get('http://localhost:5000/news/allnews')
            .then(response => {
                console.log(response.data)
                const news = response.data;
                this.setState({ news });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    NewsList() {
        return this.state.news.map(currentNews => {
            if (currentNews.id !== null) {
                return <Card key={currentNews.rid} news={currentNews} />;
            }
        })
    }

    render() {
        return (
            <div >


            </div>
        )
    }
}
